import styles from "./Modal.module.css";
import {Fragment} from 'react';
import  ReactDOM  from 'react';

const Backdrop = (props) => {
  return <div className={styles.backdrop}></div>;
};

const ModaOverlay = (props) => {
  return (
    <div className={styles.modal}>
      <div className={styles.content}>{props.children}</div>
    </div>
  );
};
//  this is the oen which we have named in index.html
const portalElement = document.getElementById('overlays')
const Modal = (props) =>
{
    return <Fragment>
   {/* <Backdrop></Backdrop>
<ModaOverlay>{props.children}</ModaOverlay>
here we are using directly while we can use portal also like i am doing now*/}

    {ReactDOM.createPortal(<Backdrop/>, portalElement )}
    {ReactDOM.createPortal(<ModaOverlay>{props.children}</ModaOverlay>)}

    </Fragment>
}

export default Modal;
