import Button from './Button';
import Card from './Card';
import styles from './ErrorModal.module.css'

const ErrorModal = (props) =>
{
    return (
        <Card>
            <header>
            <h2>{props.title}</h2>
            </header>
            <div>
            {props.message}
            </div>

            <footer>
            <Button/>
            </footer>


        </Card>
        
        
    )
}

export default ErrorModal;
